import os, numpy, pathlib, PIL.Image; [print(''.join([''.join([[i for i in " .:-=+*#%@"][j] for j in i]) +'\n' for i in numpy.multiply(numpy.divide(numpy.array(PIL.Image.open(path).resize(os.get_terminal_size()).convert('L')), 256), len([i for i in " .:-=+*#%@"])-1).astype(int)]).removesuffix('\n')) for path in [f'{pathlib.Path(__file__).parent.resolve()}\\data\\{z}' for z in os.listdir(f'{pathlib.Path(__file__).parent.resolve()}\\data')]]